PRAGMA defer_foreign_keys=TRUE;
CREATE TABLE CaseOfStudies (   id                INTEGER PRIMARY KEY AUTOINCREMENT,   slug              TEXT,   title             TEXT,   summary           TEXT,   image_path        TEXT,   detail_image_path TEXT,   position_top      REAL,   position_left     REAL,   color             TEXT,  is_published      INTEGER,  created_at        DATETIME,   updated_at        DATETIME );
CREATE TABLE Zones (   id            INTEGER PRIMARY KEY AUTOINCREMENT,   slug          TEXT,   case_study_id INTEGER,   title         TEXT,   image_path    TEXT,   position_top  REAL,   position_left REAL,   created_at    DATETIME,   updated_at    DATETIME,   FOREIGN KEY (case_study_id) REFERENCES CaseOfStudies(id) );
CREATE TABLE AffectationTypes (   id          INTEGER PRIMARY KEY AUTOINCREMENT,   slug        TEXT,   name        TEXT,   icon_path   TEXT,   description TEXT,   created_at  DATETIME,   updated_at  DATETIME );
CREATE TABLE Scenes (   id                       INTEGER PRIMARY KEY AUTOINCREMENT,   slug                     TEXT,   zone_id                  INTEGER,   affectation_type_id      INTEGER,   title                    TEXT,   image_path               TEXT,   position_top             REAL,   position_left            REAL,   decoration_image_path    TEXT,   decoration_position_top  REAL,   decoration_position_left REAL,   decoration_width_vw      REAL,   decoration_tooltip       TEXT,   created_at               DATETIME,   updated_at               DATETIME,   FOREIGN KEY (zone_id)             REFERENCES Zones(id),   FOREIGN KEY (affectation_type_id) REFERENCES AffectationTypes(id) );
CREATE TABLE Elements (   id                 INTEGER PRIMARY KEY AUTOINCREMENT,   slug               TEXT,   scene_id           INTEGER,   affectation_type_id INTEGER,   title              TEXT,   subtitle           TEXT,   description        TEXT,   source             TEXT,   image_path         TEXT,   detail_image_path  TEXT,   position_top       REAL,   position_left      REAL,   created_at         DATETIME,   updated_at         DATETIME,   FOREIGN KEY (scene_id)            REFERENCES Scenes(id),   FOREIGN KEY (affectation_type_id) REFERENCES AffectationTypes(id) );
CREATE TABLE Tags (   id         INTEGER PRIMARY KEY AUTOINCREMENT,   name       TEXT,   created_at DATETIME,   updated_at DATETIME );
CREATE TABLE ElementTags (   id         INTEGER PRIMARY KEY AUTOINCREMENT,   element_id INTEGER,   tag_id     INTEGER,   created_at DATETIME,   updated_at DATETIME,   FOREIGN KEY (element_id) REFERENCES Elements(id),   FOREIGN KEY (tag_id)     REFERENCES Tags(id) );
DELETE FROM sqlite_sequence;
